import React, { Component } from "react";
import NavBar from "./NavBar";
import { newFriend } from "../services/userService";
import { toast } from "react-toastify";

class Friends extends Component {
  state = {
    formData: {
      title: "",
      bio: "",
      summary: "",
      headline: "",
      slug: "",
      status: "",
      skills: "",
      primaryImage: ""
    }
  }

  onFormFieldChange = (e) => {
    console.log(e.currentTarget.name, e.currentTarget.value);
    const currentTarget = e.currentTarget;
    const newValue =
      currentTarget.type === "checkbox"
        ? currentTarget.checked
        : currentTarget.value;
    const inputName = currentTarget.name;

    this.setState(() => {
      const formData = { ...this.state.formData };
      formData[inputName] = newValue;
      return { formData };
    });
  };

  onSubmitFormButtonClicked = (e) => {
    e.preventDefault();
    console.log("you clicked submit");
    console.log("current State: ", this.state.formData);
    if (this.state.formData.title === "" ||
      this.state.formData.bio === "" ||
      this.state.formData.summary === "" ||
      this.state.formData.headline === "" ||
      this.state.formData.slug === "" ||
      this.state.formData.status === "" ||
      this.state.formData.skills === "" ||
      this.state.formData.primaryImage === ""
    ) {
      toast.error("All fields must be filled")


    } else {

      newFriend(this.state.formData)
        .then(this.onNewFriendSuccess)
        .catch(this.onNewFriendError);
    }
  };

  onNewFriendSuccess = (response) => {
    toast.success(`Congratulations you added ${this.state.formData.title}`);
    // toast.info("Would you like to add another friend?");
    console.log({ newFriendAdded: response });
    this.props.history.push("/welcome")
  };
  onNewFriendError = (response) => {
    // if (this.state.formData[this.state.formData.inputName] === "") {
    //   console.log("field empty")
    // }

    toast.error("Your new friend was not added. Please try again");
    console.warn({ errorAddingFriend: response })
  }


  render() {
    return (
      <React.Fragment>
        <NavBar />
        <div
          className="jumbotron jumbotron-fluid"
          style={{ backgroundColor: "#b4aee8" }}
        >
          <div className="container">
            <h1 className="display-3">Friends</h1>
            <p className="lead">
              Welcome to your Friends page... Here you have an opportunity to
              make a list of your favorite people
            </p>
          </div>
        </div>
        <div className="container">
          <h1>Add or Edit a Friend</h1>
          <div className="row">
            <div className="col form-group">
              <form id="FriendsForm">

                <div className="form-group row">
                  <label htmlFor="title">Title:</label>
                  <div className="col-sm-10">
                    <input
                      type="text"
                      name="title"
                      onChange={this.onFormFieldChange}
                    />
                  </div>
                </div>

                <div className="form-group row">
                  <label htmlFor="bio">Bio:</label>
                  <div className="col-sm-10">
                    <input
                      type="text"
                      name="bio"
                      onChange={this.onFormFieldChange}
                    />
                  </div>
                </div>

                <div className="form-group row">
                  <label htmlFor="summary">Summary:</label>
                  <div className="col-sm-10">
                    <input
                      type="text"
                      name="summary"
                      onChange={this.onFormFieldChange}
                    />
                  </div>
                </div>

                <div className="form-group row">
                  <label htmlFor="headline">Headline:</label>
                  <div className="col-sm-10">
                    <input
                      type="text"
                      name="headline"
                      onChange={this.onFormFieldChange}
                    />
                  </div>
                </div>

                <div className="form-group row">
                  <label htmlFor="slug">Slug:</label>
                  <div className="col-sm-10">
                    <input
                      type="text"
                      name="slug"
                      onChange={this.onFormFieldChange}
                    />
                  </div>
                </div>

                <div className="form-group row">
                  <label htmlFor="status">Status:</label>
                  <div className="col-sm-10">
                    <input
                      type="text"
                      name="status"
                      onChange={this.onFormFieldChange}
                    />
                  </div>
                </div>

                <div className="form-group row">
                  <label htmlFor="skills">Skills:</label>
                  <div className="col-sm-10">
                    <input
                      type="text"
                      name="skills"
                      onChange={this.onFormFieldChange}
                    />
                  </div>
                </div>

                <div className="form-group row">
                  <label htmlFor="primaryImage">Primary Image:</label>
                  <div className="col-sm-10">
                    <input
                      type="text"
                      name="primaryImage"
                      onChange={this.onFormFieldChange}
                    />
                  </div>
                </div>



                <button
                  type="submit"
                  className="btn btn-warning"
                  style={{ backgroundColor: "#b4aee8" }}
                  onClick={this.onSubmitFormButtonClicked}
                >
                  Submit
                </button>
              </form>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Friends;
