import axios from "axios";

var userService = {
    endpoint: "https://api.remotebootcamp.dev/api/",


}

const login = (payload) => {
    const config = {
        method: "POST",
        url: userService.endpoint + "users/login",
        data: payload,
        crossdomain: true,
        headers: { "Content-Type": "application/json" }
    };
    return axios(config)

}

const register = (payload) => {
    const config = {
        method: "POST",
        url: userService.endpoint + "users/register",
        data: payload,
        crossdomain: true,
        headers: { "Content-Type": "application/json" }
    };
    return axios(config)
}

const logout = () => {
    const config = {
        method: "GET",
        url: userService.endpoint + "users/logout",
        crossdomain: true
    };
    return axios(config)

}

const current = () => {
    const config = {
        method: "GET",
        url: userService.endpoint + "users/current",
        crossdomain: true
    };
    return axios(config)
}

const newEntity = (payload) => {
    const config = {
        method: "POST",
        url: userService.endpoint + "entity/Machines",
        data: payload,
        crossdomain: true,
        headers: { "Content-Type": "application/json" }
    }
    return axios(config);
}

const newFriend = (payload) => {
    const config = {
        method: "POST",
        url: userService.endpoint + "friends",
        data: payload,
        crossdomain: true,
        headers: { "Content-Type": "application/json" }
    }
    return axios(config);
}

export {
    login,
    logout,
    current,
    register,
    newEntity,
    newFriend,
}
