import React, { Component } from "react";
import { allFriends } from "../services/userService";
import NavBar from "./NavBar";
import OneFriendCard from "./OneFriendCard";

class FriendsList extends Component {
    state = {
        names: ["Juan Pablo", "Germosen", "Perez"],

    }

    componentDidMount() {

        allFriends()
            .then(this.onAllFriendsSuccess)
            .catch(this.onAllFriendsError)
    }

    onAllFriendsSuccess = (response) => {
        console.log("all friends success", response.data.item.pagedItems)
        let myPresidents = response.data.item.pagedItems;

        this.setState((preState) => {
            return { mappedPresidents: myPresidents.map(this.mapFriend) }
        })
    }
    onAllFriendsError = (response) => {
        console.log("all friends error", response)
    }


    mapFriend = (onePresident) => {
        return (
            <div key={onePresident.id} className="card col-md-2 m-4">
                <img src={onePresident.primaryImage.imageUrl} className="card-img-top" alt="..." />
                <div className="card-body">
                    <h5 className="card-title">{onePresident.title}</h5>
                    <strong>{onePresident.headline}</strong>
                    <p className="card-text">{onePresident.summary}</p>
                    <div>
                        <button className="btn btn-danger m-1">Delete</button>
                        <span><button className="btn btn-info m-1">Edit</button></span>
                    </div>
                </div>
            </div >
        )
    };
    mapFriendSimple = (onePresident) => {
        return <p key={`Presidents-${onePresident.id}`}>{onePresident.nm}</p>;
    };



    render() {
        return (
            <React.Fragment>
                <NavBar />
                <div className="col-md-12 p-5">
                    <h1>Friend List</h1>
                    <hr />
                    <div className="row">
                        {this.state.mappedPresidents}
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default FriendsList;
