import React, { Component } from "react";
// import { allFriends } from "../services/userService";

class FriendsList extends Component {
    state = {
        names: ["Juan Pablo", "Germosen", "Perez"],
        presidents: [
            {
                id: 1,
                nm: "Juan Pablo",
                pp: "None, Federalist",
                tm: "1789-1797",
                avatar: "https://upload.wikimedia.org/wikipedia/commons/thumb/3/36/Two_red_dice_01.svg/800px-Two_red_dice_01.svg.png"
            },
            {
                id: 2,
                nm: "Pablo Pablo",
                pp: "One, Federalist",
                tm: "1789-308978",
                avatar: "https://media.gq.com/photos/5d3717d1802ac200082431ff/16:9/w_2560%2Cc_limit/how-to-cool-down-your-body-in-a-heat-wave-gq.jpg"
            },
            {
                id: 3,
                nm: "Pedro Juan",
                pp: "Other, This one",
                tm: "2021-1987",
                avatar: "https://www.newstechnologyservices.com/wp-content/uploads/2019/08/fun@work.png"
            },
        ]
    }

    // componentDidMount = () => {
    //     console.log("componentDidMount is going to get all the friends")
    //     allFriends()
    //         .then(this.onAllFriendsSuccess)
    //         .catch(this.onAllFriendsError)
    // };

    onAllFriendsSuccess = (response) => {
        console.log("all friends success", response.data.item.pagedItems)


        // this.setState(() => {
        //     let friendsArray = { ...this.state.friendsArray };
        //     friendsArray = [response.data.item.pagedItems];
        //     console.log("newState:", this.state.friendsArray)
        //     return { friendsArray };
        // })
    }
    onAllFriendsError = (response) => {
        console.log("all friends error", response)
    }

    mapPresident = (onePresident) => {
        return <div className="card col-md-3">
            <img src={onePresident.avatar} className="card-img-top" alt="..." />
            <div className="card-body">
                <h5 className="card-title">{onePresident.nm}</h5>
                {onePresident.pp}
                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                <button className="btn btn-info">Info</button>
            </div>
        </div>
    };
    mapPresidentSimple = (onePresident) => {
        return <p key={`Presidents-${onePresident.id}`}>{onePresident.nm}</p>;
    };



    render() {
        return (
            <div className="col-md-12 p-5">
                <h1>Presidents</h1>
                <hr />
                <div className="row">
                    {this.state.presidents.map(this.mapPresident)}
                </div>
            </div>
        );
    }
}

export default FriendsList;
