import React, { Component } from "react";
import { allFriends } from "../services/userService";

class FriendsList extends Component {
    state = {
        names: ["Juan Pablo", "Germosen", "Perez"],
        // presidents: [
        //     {
        //         id: 1,
        //         nm: "Juan Pablo",
        //         pp: "None, Federalist",
        //         tm: "1789-1797",
        //         avatar: "https://upload.wikimedia.org/wikipedia/commons/thumb/3/36/Two_red_dice_01.svg/800px-Two_red_dice_01.svg.png"
