        //     },
        //     {
        //         id: 3,
        //         nm: "Pedro Juan",
        //         pp: "Other, This one",
        //         tm: "2021-1987",
        //         avatar: "https://www.newstechnologyservices.com/wp-content/uploads/2019/08/fun@work.png"
        //     },
        // ]
    }

    componentDidMount() {
        // this.setState((prevState) => {
        //     return { mappedPresidents: prevState.presidents.map(this.mapPresident) }
        // })
        allFriends()
            .then(this.onAllFriendsSuccess)
            .catch(this.onAllFriendsError)
    }

    onAllFriendsSuccess = (response) => {
        console.log("all friends success", response.data.item.pagedItems)
    }
    onAllFriendsError = (response) => {
        console.log("all friends error", response)
    }

    // componentDidMount = () => {
    //     console.log("componentDidMount is going to get all the friends")
    //     allFriends()
    //         .then(this.onAllFriendsSuccess)
    //         .catch(this.onAllFriendsError)
    // };


    // this.setState(() => {
    //     let friendsArray = { ...this.state.friendsArray };
    //     friendsArray = [response.data.item.pagedItems];
    //     console.log("newState:", this.state.friendsArray)
    //     return { friendsArray };
    // })
    // }

    mapPresident = (onePresident) => {
        return (
            <div key={onePresident.id} className="card col-md-3">
                <img src={onePresident.avatar} className="card-img-top" alt="..." />
                <div className="card-body">
                    <h5 className="card-title">{onePresident.nm}</h5>
                    <strong>{onePresident.pp}</strong>
                    <p className="card-text">Some quick example text to build on the card title .</p>
                    <button className="btn btn-info">Info</button>
                </div>
            </div>
        )
    };
    mapPresidentSimple = (onePresident) => {
        return <p key={`Presidents-${onePresident.id}`}>{onePresident.nm}</p>;
    };



    render() {
        return (
            <div className="col-md-12 p-5">
                <h1>Presidents</h1>
                <hr />
                <div className="row">
                    {/* {this.state.presidents.map(this.mapPresident)} */}
                    {this.state.mappedPresidents}
                </div>
            </div>
        );
    }
}

export default FriendsList;
