        names: ["Juan Pablo", "Germosen", "Perez"],
        presidents: [
            {
                id: 1,
                nm: "Juan Pablo",
                pp: "None, Federalist",
                tm: "1789-1797"
            },
            {
                id: 2,
                nm: "Pablo Pablo",
                pp: "One, Federalist",
                tm: "1789-308978"
            },
            {
                id: 3,
                nm: "Pedro Juan",
                pp: "Other, This one",
                tm: "2021-1987"
            },
        ]
    }

