import React, { Component } from "react";
import entityService from "../services/entityService";
import NavBar from "./NavBar";
import OneProductCard from "./OneProductCard";
import { toast } from "react-toastify";
// import OneProductCard from "./OneProductCard";

class ProductList extends Component {
    state = {};


    componentDidMount() {
        entityService.getAll()
            .then(this.onGetAllSuccess)
            .catch(this.onGetAllError)
    }

    onGetAllSuccess = (response) => {
        console.log("all products success ", response.data.items)
        const myProducts = response.data.items;

        this.setState((prevState) => {
            return { mappedProducts: myProducts.map(this.mapProduct) }
        })
        // console.log("this is mappedProducts: ", myProducts.map(this.mapProduct))
    }

    onGetAllError = (response) => {
        console.log("all products error ", response)
    }

    mapProduct = (oneProduct) => {
        return (
            <OneProductCard
                key={oneProduct.id}
                name={oneProduct.name}
                manufacturer={oneProduct.manufacturer}
                description={oneProduct.description}
                cost={oneProduct.cost}
                id={oneProduct.id}
                delete={this.deleteOne}
            />

        )
    }
    // onDeleteButtonClicked = (e) => {
    //     e.preventDefault()
    //     console.log("you clicked delete")
    // }

    deleteOne(id, e) {
        console.log("deleteOne running from Product List", id)
        console.log("deleteOne running from Product List", e)
        entityService.delete(id)
            .then(this.onDeleteButtonClickedSuccess)
            .catch(this.onDeleteButtonClickedError)
    }
    onDeleteButtonClickedSuccess = (response) => {
        toast.success("item deleted")
        console.log({ deletedItem: response })
        this.props.history.push("/productList")
    }
    onDeleteButtonClickedError = (response) => {
        toast.error("Product not deleted. Please try again");
        console.warn({ errorDeletingProduct: response })
    }




    render() {
        return (
            <React.Fragment>
                <NavBar />
                <div className="col-md-12 p-5">
                    <h1>Product List</h1>
                    <hr />
                    <div className="row">
                        {this.state.mappedProducts}
                    </div>
                </div>
            </React.Fragment>

        )
    }

}
export default ProductList;
