import React, { Component } from "react";
import { allFriends } from "../services/userService";

class FriendsList extends Component {
    state = {
        friendsArray: [],
    }

    componentDidMount = () => {
        console.log("componentDidMount is going to get all the friends")
        allFriends()
            .then(this.onAllFriendsSuccess)
            .catch(this.onAllFriendsError)
    };

    onClickButtonAllFriends = (e) => {
        console.log("you clicked the button")
        allFriends()
            .then(this.onAllFriendsSuccess)
            .catch(this.onAllFriendsError)
    }
    onAllFriendsSuccess = (response) => {
        console.log("all friends success", response.data.item.pagedItems)
        let responseArrayFriends = response.data.item.pagedItems;

        this.setState(() => {
            let friendsArray = { ...this.state.friendsArray };
            friendsArray = response.data.item.pagedItems;
            return { friendsArray };
            console.log({ newState: this.state.friendsArray })
        })
    }
    onAllFriendsError = (response) => {
        console.log("all friends error", response)
    }





    render() {
        return (
            <div>
                <button
                    type="button"
                    onClick={this.onClickButtonAllFriends}
                    className="btn btn-info m-5"
                >
                    all friends
                </button>
            </div>

        )
    }
}

export default FriendsList;
