import React, { Component } from "react";


class OneProductCard extends Component {


    onDeleteButtonClicked = (e) => {
        if (window.confirm("Sure you want to delete?")) {

            console.log("clicked delete in OneProduct", this.props.id)
            this.props.delete()
        }
    }
    render() {
        return (
            <div key={this.props.id} className="card col-md-2 m-4">
                <div className="card-body">
                    <h5 className="card-title">{this.props.name}</h5>
                    <p style={{ color: "red" }}>Id: {this.props.id}</p>
                    <strong>{this.props.manufacturer}</strong>
                    <p style={{ color: "blue" }} className="card-text">{this.props.description}</p>
                    <p style={{ color: "green" }}>Price: {this.props.cost}, Id: {this.props.id}</p>
                    <div>
                        <button
                            className="btn btn-danger m-1"
                            onClick={this.onDeleteButtonClicked(this.props.id)}
                        >
                            Delete
                        </button>
                        <button
                            className="btn btn-info m-1"
                        >
                            Edit
                        </button>
                        <div
                            className="row">

                        </div>
                    </div>
                </div>
            </div >
        )
    }

}

export default OneProductCard;
