import React, { Component } from "react";
import friendsService from "../services/friendsService";
import NavBar from "./NavBar";
import OneFriendCard from "./OneFriendCard";



class FriendsList extends Component {
    state = {
        names: ["Juan Pablo", "Germosen", "Perez"],
    }


    componentDidMount() {
        friendsService.allFriends()
            .then(this.onAllFriendsSuccess)
            .catch(this.onAllFriendsError)
    }

    onAllFriendsSuccess = (response) => {
        console.log("all friends success", response.data.item.pagedItems)
        let myFriends = response.data.item.pagedItems;

        this.setState((preState) => {
            return { mappedFriends: myFriends.map(this.mapFriend) }
        })
        console.log("this is mappedFriends", myFriends.map(this.mapFriend))
    }
    onAllFriendsError = (response) => {
        console.log("all friends error", response)
    }

    mapFriend = (oneFriend) => {
        return (
            // functional component from that child component
            <OneFriendCard
                key={oneFriend.id}
                title={oneFriend.title}
                imgSrc={oneFriend.primaryImage.imageUrl}
                headline={oneFriend.headline}
                summary={oneFriend.summary}

            />
            // <div key={oneFriend.id} className="card col-md-2 m-4">
            //     <img src={oneFriend.primaryImage.imageUrl} className="card-img-top" alt="..." />
            //     <div className="card-body">
            //         <h5 className="card-title">{oneFriend.title}</h5>
            //         <strong>{oneFriend.headline}</strong>
            //         <p className="card-text">{oneFriend.summary}</p>
            //         <div>
            //             <button className="btn btn-danger m-1">Delete</button>
            //             <span><button className="btn btn-info m-1">Edit</button></span>
            //         </div>
            //     </div>
            // </div >
        )
    };
    mapFriendSimple = (oneFriend) => {
        return <p key={`Presidents-${oneFriend.id}`}>{oneFriend.nm}</p>;
    };



    render() {
        return (
            <React.Fragment>
                <NavBar />
                <div className="col-md-12 p-5">
                    <h1>Friend List</h1>
                    <hr />
                    <div className="row">
                        {this.state.mappedFriends}
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default FriendsList;
